package com.zhangwei.emojitool;

import java.io.FileOutputStream;
import java.io.IOException;

public class EmojiTool {
    public static void main(String[] args) {
        int codePoint = 0x1f232;
        StringBuilder sb = new StringBuilder();
        /*for(int i=0;i<100;i++){
            sb.append(Character.toChars(codePoint+i));
        }*/
        String codepointstr = "";
        String[] codepoints = codepointstr.split("U\\+");
        int count = 0;
        for(String codepoint : codepoints){
            if(codepoint.length()<1){
                continue;
            }
            if((count++)%40==0){
                sb.append("\n\n");
            }
            sb.append(Character.toChars(Integer.parseInt(codepoint,16)));
        }
        try(FileOutputStream out = new FileOutputStream("/tem_file/emoji.txt");) {
            byte[] bytes = sb.toString().getBytes();
            out.write(bytes);
        } catch (IOException e) {
            e.printStackTrace();
        }
    }
}
